// Kronos API Authentication Example
const API_KEY = 'your-api-key-here';
const BASE_URL = 'https://api.kronos.example.com/v1';

// Function to make authenticated requests
async function authenticatedRequest(endpoint, options = {}) {
  const headers = {
    'Authorization': `Bearer ${API_KEY}`,
    'Content-Type': 'application/json',
    ...options.headers
  };

  try {
    const response = await fetch(`${BASE_URL}${endpoint}`, {
      ...options,
      headers
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    return await response.json();
  } catch (error) {
    console.error('API request failed:', error);
    throw error;
  }
}

// Example usage
authenticatedRequest('/users')
  .then(data => console.log('Users:', data))
  .catch(error => console.error('Error:', error));
```

```python
import requests
import json
from typing import Dict, Any, Optional

class KronosAPIError(Exception):
    """Custom exception for Kronos API errors"""
    def __init__(self, status_code: int, message: str, details: Optional[Dict] = None):
        self.status_code = status_code
        self.message = message
        self.details = details or {}
        super().__init__(f"API Error {status_code}: {message}")

class KronosAPIClient:
    def __init__(self, api_key: str, base_url: str = "https://api.kronos.example.com/v1"):
        self.api_key = api_key
        self.base_url = base_url
        self.session = requests.Session()
        self.session.headers.update({
            'Authorization': f'Bearer {api_key}',
            'Content-Type': 'application/json'
        })

    def _handle_response(self, response: requests.Response) -> Dict[str, Any]:
        """Handle API response with proper error handling"""
        try:
            data = response.json()
        except json.JSONDecodeError:
            data = {"message": response.text}

        if response.status_code == 200:
            return data
        elif response.status_code == 400:
            raise KronosAPIError(400, "Bad Request", data)
        elif response.status_code == 401:
            raise KronosAPIError(401, "Unauthorized - Check your API key", data)
        elif response.status_code == 403:
            raise KronosAPIError(403, "Forbidden - Insufficient permissions", data)
        elif response.status_code == 404:
            raise KronosAPIError(404, "Resource not found", data)
        elif response.status_code == 429:
            raise KronosAPIError(429, "Rate limit exceeded", data)
        elif response.status_code >= 500:
            raise KronosAPIError(response.status_code, "Server error", data)
        else:
            raise KronosAPIError(response.status_code, "Unknown error", data)

    def get_users(self) -> Dict[str, Any]:
        """Get users with proper error handling"""
        try:
            response = self.session.get(f"{self.base_url}/users")
            return self._handle_response(response)
        except requests.RequestException as e:
            raise KronosAPIError(0, f"Network error: {str(e)}")

# Example usage
try:
    client = KronosAPIClient("your-api-key-here")
    users = client.get_users()
    print("Success:", users)
except KronosAPIError as e:
    print(f"API Error: {e}")
    print(f"Details: {e.details}")
except Exception as e:
    print(f"Unexpected error: {e}")
