{
  "openapi": "3.0.3",
  "info": {
    "title": "Kronos Banking API",
    "description": "Core banking API for account management, transactions, and customer services",
    "version": "1.0.0",
    "contact": {
      "name": "Kronos API Team",
      "email": "api-support@kronos.com",
      "url": "https://api.kronos.com/support"
    },
    "license": {
      "name": "Proprietary",
      "url": "https://kronos.com/license"
    }
  },
  "servers": [
    {
      "url": "https://api.kronos.example.com/v1",
      "description": "Production server"
    },
    {
      "url": "https://api-staging.kronos.example.com/v1",
      "description": "Staging server"
    }
  ],
  "paths": {
    "/accounts": {
      "get": {
        "summary": "List customer accounts",
        "description": "Retrieve a list of accounts for the authenticated customer",
        "operationId": "listAccounts",
        "tags": ["Accounts"],
        "security": [{ "bearerAuth": [] }],
        "responses": {
          "200": {
            "description": "List of accounts",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "accounts": {
                      "type": "array",
                      "items": { "$ref": "#/components/schemas/Account" }
                    }
                  }
                }
              }
            }
          },
          "401": { "$ref": "#/components/responses/Unauthorized" },
          "429": { "$ref": "#/components/responses/RateLimited" }
        }
      }
    },
    "/accounts/{accountId}/balance": {
      "get": {
        "summary": "Get account balance",
        "description": "Retrieve the current balance for a specific account",
        "operationId": "getAccountBalance",
        "tags": ["Accounts"],
        "security": [{ "bearerAuth": [] }],
        "parameters": [
          {
            "name": "accountId",
            "in": "path",
            "required": true,
            "schema": { "type": "string" },
            "description": "Unique account identifier"
          }
        ],
        "responses": {
          "200": {
            "description": "Account balance",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/Balance" }
              }
            }
          },
          "404": { "$ref": "#/components/responses/NotFound" }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Account": {
        "type": "object",
        "properties": {
          "id": { "type": "string" },
          "name": { "type": "string" },
          "type": { "type": "string", "enum": ["checking", "savings", "credit"] },
          "currency": { "type": "string" }
        }
      },
      "Balance": {
        "type": "object",
        "properties": {
          "available": { "type": "number" },
          "current": { "type": "number" },
          "currency": { "type": "string" }
        }
      }
    },
    "responses": {
      "Unauthorized": {
        "description": "Authentication required"
      },
      "NotFound": {
        "description": "Resource not found"
      },
      "RateLimited": {
        "description": "Rate limit exceeded"
      }
    },
    "securitySchemes": {
      "bearerAuth": {
        "type": "http",
        "scheme": "bearer",
        "bearerFormat": "JWT"
      }
    }
  }
}

