  "openapi": "3.0.3",
  "info": {
    "title": "Kronos User Management API",
    "description": "User authentication, profile management, and access control API",
    "version": "2.0.0"
  },
  "servers": [
    {
      "url": "https://api.kronos.example.com/v2/users",
      "description": "Production server"
    }
  ],
  "paths": {
    "/profile": {
      "get": {
        "summary": "Get user profile",
        "operationId": "getUserProfile",
        "tags": ["Profile"],
        "security": [{ "bearerAuth": [] }],
        "responses": {
          "200": {
            "description": "User profile",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/UserProfile" }
              }
            }
          }
        }
      },
      "put": {
        "summary": "Update user profile",
        "operationId": "updateUserProfile",
        "tags": ["Profile"],
        "security": [{ "bearerAuth": [] }],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": { "$ref": "#/components/schemas/UserProfileUpdate" }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Profile updated",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/UserProfile" }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "UserProfile": {
        "type": "object",
        "properties": {
          "id": { "type": "string" },
          "email": { "type": "string", "format": "email" },
          "firstName": { "type": "string" },
          "lastName": { "type": "string" },
          "phone": { "type": "string" },
          "createdAt": { "type": "string", "format": "date-time" }
        }
      },
      "UserProfileUpdate": {
        "type": "object",
        "properties": {
          "firstName": { "type": "string" },
          "lastName": { "type": "string" },
          "phone": { "type": "string" }
        }
      }
    },
    "securitySchemes": {
      "bearerAuth": {
        "type": "http",
        "scheme": "bearer"
      }
    }
  }
}
